<?php

namespace Gushh\CoreBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * PromotionConditionExpressionRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PromotionConditionExpressionRepository extends EntityRepository
{

  /**
   * Get all Conditions of specific group
   *
   * @return array 
   */
  public function findGroup($group)
  {

    return $this->getEntityManager()
                ->getRepository('GushhCoreBundle:PromotionConditionExpression')
                ->createQueryBuilder('p')
                ->where('p.group = :group')
                ->setParameter('group', $group)
                ->orderBy('p.name', 'ASC');
  }
}
